#!/usr/bin/env python3
#  -*- coding: utf-8 -*-
import threading

from tkinter import *
import time

event = threading.Event()


def affichage(parole):                    # Methode affichage TKinter
    fenetre = Tk()
    champ_label = Label(fenetre, text=parole)
    champ_label.pack()
    fenetre.mainloop()


def chargement_loop(stop_event, arg):
    while not stop_event.wait(1):
        print(".")


def chargement_call_and_kill():
    pill_2_kill = threading.Event()
    thread = threading.Thread(target=chargement_loop, args=(pill_2_kill, event))
    thread.start()
    time.sleep(8)
    pill_2_kill.set()
    thread.join()


